mapper.select:
SELECT id, project_id, user_id, joined_time
FROM project_member
WHERE project_id = #{projectId}
AND user_id = #{userId}

SELECT u.id, u.community_id, u.first_name, u.last_name, u.email, u.role, u.country, u.create_time FROM user AS u
INNER JOIN project_member AS pm ON u.id = pm.user_id
WHERE pm.project_id = #{projectId}

mapper.delete:
DELETE FROM project_member
WHERE project_id = #{projectId}
AND user_id = #{userId}
